import { HomemainLayout, HomeBannerHeader } from "./styles";
import HomeBoardForm from "@organisms/HomeBoardForm";
import HomeBanner from "@molecules/HomeBanner";
import { FC, lazy } from "react";
import { HomeBoardData } from "@type/Tb";
import Button from "@atom/Button";
import MainIcon from "@atom/MainIcon";
import { useNavigate } from "react-router-dom";
import HomeNoneWrite from "@organisms/HomeNoneWrite";

const HomeMain: FC<HomeBoardData> = ({ BoardData, setIsActive, isActive }) => {
  const Navigate = useNavigate();

  const HallymData = BoardData?.filter(
    (post) => post.UploadTag === "한림대학교"
  );

  const OtherData = BoardData?.filter((post) => post.UploadTag === "공모전");

  return (
    <HomemainLayout>
      <HomeBannerHeader>
        <MainIcon />
        <Button
          type={"button"}
          buttontype={"icon"}
          icon="data:image/png;base64,"
          disabled={false}
          onClick={() => Navigate("/search")}
        />
      </HomeBannerHeader>
      <HomeBanner isactive={isActive} onclick={() => setIsActive(!isActive)} />
      {BoardData?.length === 0 ? (
        <HomeNoneWrite />
      ) : isActive === true ? (
        HallymData?.map((data) => {
          return (
            <HomeBoardForm
              onclick={() =>
                Navigate("/posting", { state: { id: 1, name: data.writeid } })
              }
              name={data.Writer}
              position="팀장"
              title={data.Title}
              date={data.createdAt}
              iscruiting={data.IsRecruiting}
              subtitle={data.Detail}
              needposistion={data.NeedPosistion}
              writeid={data.writeid}
            />
          );
        })
      ) : (
        OtherData?.map((data) => {
          return (
            <HomeBoardForm
              onclick={() =>
                Navigate("/posting", { state: { id: 1, name: data.writeid } })
              }
              name={data.Writer}
              position="팀장"
              title={data.Title}
              date={data.createdAt}
              iscruiting={data.IsRecruiting}
              subtitle={data.Detail}
              needposistion={data.NeedPosistion}
            />
          );
        })
      )}
    </HomemainLayout>
  );
};

export default HomeMain;
